package com.panosmatsinopoulos.asynchronousflows

import kotlinx.coroutines.delay
import kotlinx.coroutines.runBlocking

suspend fun simple(): List<Int> {
    delay(1_000L)
    return listOf(1, 2, 3)
}

fun main() {
    println("Main starting...")
    runBlocking {
        simple().forEach { value -> println(value) }
    }
    println("Main ending")
}
